import React, { useState } from 'react'
import MovieController from '../controller/MovieController'
import MovieItems from './MovieItems';

function MovieList() {
    
const [data, setdata] = useState({movies:[],loading:false,error:null})

const fetchMovies = async() => {
try{
 const movies =   await MovieController.fetchMovie()
    setdata({movies:movies,loading:false})

}
    catch(error){
        setdata({error:error.message,loading:false})
    }
 }
console.log(data);


  return (
    <>
    <p>{data.loading?'Loading...':false}</p>
    <p>{data.error?data.error.message:null}</p>
    {data.movies.map((movie) => { <MovieItems /> })}
    </>
  )
}

export default MovieList
